uint8_t check_point(uint8_t x, uint8_t y){

	switch(sprite){
		case SPRITE_1:
			if((x>=bidoof_1_col_pos) && (x<=bidoof_1_col_pos+BIDOOF_1_COLUMNS) && (y>=bidoof_1_row_pos) && (y<=bidoof_1_row_pos+BIDOOF_1_ROWS)){
				return 1;
			}
			return 0;
		case SPRITE_2:
			if((x>=bidoof_2_col_pos) && (x<=bidoof_2_col_pos+BIDOOF_2_COLUMNS) && (y>=bidoof_2_row_pos) && (y<=bidoof_2_row_pos+BIDOOF_2_ROWS)){
				return 1;
			}
			return 0;
	}
	
	return 0;
	
}

